        console.log('Total Words:'+wordsCount+ 'Correct Words:'+correctWords);
    
        let wrongWords=wordsCount-correctWords;
        let nspeed= Math.round(((60/timeTaken)*correctWords));
        let ukeyStroks=text.value.length;
